import React, {useState} from 'react';
import {Link} from 'react-router-dom';
// Styles
import './SideBar.css';

export const SideBar = () => {
  // This JavaScript will get 5 random and unique clubs from our club list and display them in the sidebar.

  // List of clubs saved to the state of the component
  const [clubList, setClubList] = useState(["Chat for Change", 'Community Arts Council', 'Da Reading Club', 'Dynamic Dance Club', 'FCSC', 'Library Ambassadors', 'Model United Nations', 'Robotics Club', 'Tease Siblings', 'Tech Crew', 'Vi Real', 'Volleyball Club']);
  // List of random clubs
  const [randomClubs, setRandomClubs] = useState([]);
  // Filler for the links to be added
  const [randomClubsLinks, setRandomClubsLinks] = useState([0, 1, 2, 3, 4]);

  for (let i = 0; i < 5; i++) {
    // Gets a random club from clubList
    let selectedClub = clubList[Math.floor(Math.random()*clubList.length)];

    // Keeps getting a new club until it is unique in the list randomClubs
    while (randomClubs.includes(selectedClub)) {
      selectedClub = clubList[Math.floor(Math.random()*clubList.length)];
    }

    // Appends selectedClub to the end of the randomClubs array
    setRandomClubs([...randomClubs, selectedClub]);

    for (let i = 0; i < randomClubs.length; i++) {
      switch (randomClubs[i]) {
        case 'Chat for Change':
          randomClubsLinks[i] = '/clubs/ChatForChange';
        break;
      }
    }

  }



  return (
    <div>
      <h3>Suggested:</h3>
      <ul>
        {randomClubs.map((value, index) => {
          return <li key={index}><Link>{value}</Link></li>
        })}
      </ul>
      <Link to='/clubs/chatforchange'>Chat for Change</Link>

    </div>
  );
}
