import React, {useState} from 'react';
import {Link} from 'react-router-dom';
// Styles
import './SideBar.css';

export const SideBar = () => {
  // This JavaScript will get 5 random and unique clubs from our club list and display them in the sidebar.

  // List of clubs saved to the state of the component
  const [clubList, setClubList] = useState(["ChatForChange", 'CommunityArtsCouncil', 'DaReadingClub', 'DynamicDanceClub', 'FCSC', 'LibraryAmbassadors', 'ModelUnitedNations', 'RoboticsClub', 'TeaseSiblings', 'TechCrew', 'ViReal', 'VolleyballClub']);
  // List of random clubs
  const [randomClubs, setRandomClubs] = useState([]);

  for (let i = 0; i < 5; i++) {
    // Gets a random club from clubList
    let selectedClub = clubList[Math.floor(Math.random()*clubList.length)];

    // Keeps getting a new club until it is unique in the list randomClubs
    while (randomClubs.includes(selectedClub)) {
      selectedClub = clubList[Math.floor(Math.random()*clubList.length)];
    }

    // Appends selectedClub to the end of the randomClubs array
    randomClubs.push(selectedClub);

  }



  return (
    <div>
      <h3>Suggested:</h3>
      <Link to='/clubs/chatforchange'>Chat for Change</Link>
      <p>{randomClubs}</p>
    </div>
  );
}
